var dia = now.getDate() < 10 ? "0" + now.getDate() : now.getDate();
var anio = now.getFullYear();

if (minutos < 10)
    var tiempo = horas + ":0" + minutos;
else
    var tiempo = horas + ":" + minutos;


var fecha = dia + "/" + mes + "/" + anio;

//---- Cálculo de porcentaje de batería ----
